import React from "react";
import {
	View,
	Text,
	StyleSheet,
	SafeAreaView,
	Image,
	TouchableOpacity,
	ImageBackground,
	ScrollView,
} from "react-native";
import { COLORS, FONTS, SIZES } from "../constants/theme";
import { images } from "../constants";
import { MainTopNavigation, MainBottomNavigation } from "../navigations";
import { PostCard } from "../components";

const Groups = ({ navigation }) => {
	return (
		<SafeAreaView style={{ width: "100%", height: "100%" }}>
			<MainTopNavigation navigation={navigation} title="GROUPS" />
			<View
				resizeMode="cover"
				style={{ width: "100%", height: "100%", position: "fixed" }}
			>
				<ScrollView
					style={{
						padding: 10,
						position: "relative",
						width: "100%",
						zIndex: 44,
						paddingVertical: 60,
						overflow: "scroll",
						flexDirection: "column",
					}}
				>
					<ImageBackground
						source={images.img010}
						style={{
							height: 200,
							position: "relative",
						}}
					>
						<TouchableOpacity
							style={{
								backgroundColor: COLORS.lightGray,
								padding: 10,
								borderRadius: 30,
								marginTop: 130,
								right: 0,
								marginRight: 10,
								position: "absolute",
							}}
						>
							<Ionicons name="md-camera" size={30} color={COLORS.black} />
						</TouchableOpacity>
						<View
							style={{
								padding: 10,
								borderRadius: 30,
								marginTop: 80,
								position: "absolute",
								justifyContent: "center",
								alignItems: "center",
								width: "100%",
							}}
						>
							<Image
								source={images.edgar001}
								style={{
									height: 200,
									width: 200,
									borderRadius: 200,
									marginTop: 10,
								}}
							/>
							<TouchableOpacity
								style={{
									backgroundColor: COLORS.lightGray,
									padding: 10,
									borderRadius: 30,
									marginTop: 130,
									right: "15%",
									marginRight: 10,
									position: "absolute",
								}}
							>
								<Ionicons name="md-camera" size={30} color={COLORS.black} />
							</TouchableOpacity>
						</View>
					</ImageBackground>
				</ScrollView>
			</View>

			<MainBottomNavigation navigation={navigation} />
		</SafeAreaView>
	);
};

const styles = StyleSheet.create({});

export default Groups;
