import React, { useState } from "react";
import {
	View,
	Text,
	StyleSheet,
	SafeAreaView,
	Image,
	TouchableOpacity,
	ScrollView,
	ImageBackground,
	Alert,
	Modal,
	TextInput,
} from "react-native";
import { COLORS, FONTS, SIZES } from "../constants/theme";
import { images } from "../constants";
import { MainTopNavigation, MainBottomNavigation } from "../navigations";
import { Ionicons } from "@expo/vector-icons";
import { FollowerCard, FollowingCard, PostCard } from "../components";

const MyProfile = ({ navigation }) => {
	const [modalVisible, setModalVisible] = useState(false);
	console.log(modalVisible);
	return (
		<SafeAreaView style={{ width: "100%", height: "100%" }}>
			<MainTopNavigation
				style={{ zIndex: 10 }}
				navigation={navigation}
				title="USER PROFILE"
			/>
			<View
				resizeMode="cover"
				style={{ width: "100%", height: "100%", position: "fixed" }}
			>
				<ScrollView
					style={{
						padding: 10,
						position: "relative",
						width: "100%",
						zIndex: 44,
						paddingTop: 60,
						paddingBottom: 80,
						overflow: "scroll",
						flexDirection: "column",
					}}
				>
					<View
						style={{
							backgroundColor: COLORS.white,
							borderRadius: 5,
							padding: 0,
							marginTop: 10,
						}}
					>
						<ImageBackground
							source={images.img010}
							style={{
								height: 200,
								position: "relative",
							}}
						>
							<TouchableOpacity
								style={{
									backgroundColor: COLORS.lightGray,
									padding: 10,
									borderRadius: 30,
									marginTop: 130,
									right: 0,
									marginRight: 10,
									position: "absolute",
									zIndex: 15,
								}}
							>
								<Ionicons name="md-camera" size={30} color={COLORS.black} />
							</TouchableOpacity>
							<View
								style={{
									padding: 10,
									borderRadius: 30,
									marginTop: 80,
									position: "absolute",
									justifyContent: "center",
									alignItems: "center",
									width: "100%",
									zIndex: 11,
								}}
							>
								<Image
									source={images.edgar001}
									style={{
										height: 200,
										width: 200,
										borderRadius: 200,
										marginTop: 10,
									}}
								/>
								<TouchableOpacity
									style={{
										backgroundColor: COLORS.lightGray,
										padding: 10,
										borderRadius: 30,
										marginTop: 130,
										right: "15%",
										marginRight: 10,
										position: "absolute",
									}}
								>
									<Ionicons name="md-camera" size={30} color={COLORS.black} />
								</TouchableOpacity>
							</View>
						</ImageBackground>
						<View style={{ marginTop: 100, padding: 10 }}>
							<Text style={{ fontSize: SIZES.h2 }}>Edgar Tinkamanyire</Text>
							<TouchableOpacity
								style={{
									backgroundColor: COLORS.lightGray,
									width: "100%",
									padding: 10,
									borderRadius: 20,
									textAlign: "center",
									marginVertical: 10,
								}}
								// onPress={() => setModalVisible(true)}
							>
								<Text>Edit Profile</Text>
							</TouchableOpacity>
							<View style={{ flexDirection: "row" }}>
								<Ionicons name="md-briefcase" size={20} color={COLORS.black} />
								<Text style={{ marginVertical: 4, paddingHorizontal: 5 }}>
									Software Engineer at Pixabits
								</Text>
							</View>
							<View style={{ flexDirection: "row" }}>
								<Ionicons name="md-school" size={20} color={COLORS.black} />
								<Text style={{ marginVertical: 4, paddingHorizontal: 5 }}>
									Went to Makerere University
								</Text>
							</View>
							<View style={{ flexDirection: "row" }}>
